// weather-module.js

/** weatherオブジェクトの生成 */
var weather = {}; 

//関数をつける
weather.today = function() {
  console.log('晴れだよ');
}

//関数をつける
weather.yesterday = function() {
  console.log('雨だったよ');
}


/** weatherオブジェクトの生成 */
var weather2 = {}; 
weather2.today = function() {
  console.log('晴れだオラ');
}
weather2.yesterday = function() {
  console.log('雨だったオラ');
}


const getMessage1 = async () => {
  return "msg1!!";
};

console.log(getMessage1());

//このモジュールを外部から呼ぶ時(require時)にこれを渡す。
module.exports = weather;
//これで上書きされてるよ。↓
module.exports = weather2;


